        hgr.set = new ButtonSet(`green`, `grey`, `fa-arrow-circle-right`, `fa-times`, `Remove`, `Cancel`, startHgrRemover.bind(null, hgr), stopHgrRemover.bind(null, hgr));
        hgr.popup.description.appendChild(hgr.set.set);
