    expand = collapse.nextElementSibling;
    esgst.cerbButtons.push({
      collapse: cerb_collapseReplies.bind(null, collapse, expand, replies),
      expand: cerb_expandReplies.bind(null, collapse, expand, replies),
      permalink: permalink
    });
