      if (dm.reader) {
        dm.data = JSON.parse(blob
          ? (await readZip(blob))[0].value
          : dm.reader.result
        );
      }
      createConfirmation(`Are you sure you want to restore the selected data?`, manageData.bind(null, dm, dropbox, googleDrive, oneDrive, space, callback), callback);
