            headers: {
              authorization: `Bearer ${value}`,
              [`Dropbox-API-Arg`]: `{"path": "/${name}.zip"}`,
              [`Content-Type`]: `application/octet-stream`
            },
            method: `POST`,
            url: `https://content.dropboxapi.com/2/files/upload`
