    async checkMissingDiscussions(refresh, callback) {
        let savedDiscussions = JSON.parse(await this.getValue(`discussions`, `{}`));
        let rows = document.getElementsByClassName(`table__rows`);
        let numDiscussions = 0;
        let numDeals = 0;
        let filteredDiscussions = 0;
        let filteredDeals = 0;
        if (refresh) {
            rows[0].innerHTML = ``;
            rows[1].innerHTML = ``;
        } else {
            let preset = null;
            if (esgst.df) {
                let name = esgst.df_preset;
                if (name) {
                    let presets = JSON.parse(esgst.storage.dfPresets);
                    let i;
                    for (i = presets.length - 1; i > -1 && presets[i].name !== name; i--);
                    if (i > -1) {
                        preset = presets[i];
                    }
                }
            }
            if (preset) {
                (await this.discussions_get(rows[0], true, savedDiscussions)).forEach(discussion => {
                    if (this.df_filterDiscussion(preset, discussion)) {
                        discussion.outerWrap.remove();
                        filteredDiscussions += 1;
                    } else {
                        numDiscussions += 1;
                    }
                });
                (await this.discussions_get(rows[1], true, savedDiscussions)).forEach(deal => {
                    if (this.df_filterDiscussion(preset, deal)) {
                        deal.outerWrap.remove();
                        filteredDeals += 1;
                    } else {
                        numDeals += 1;
                    }
                });
            } else {
                numDiscussions = (await this.discussions_get(rows[0], true, savedDiscussions)).length;
                numDeals = (await this.discussions_get(rows[1], true, savedDiscussions)).length;
            }
        }
        if (numDiscussions < 5 || numDeals < 5) {
            let [response1, response2] = await Promise.all([this.request({method: `GET`, url: `/discussions`}), this.request({method: `GET`, url: `/discussions/deals`})]);
            let response1Html = this.parseHtml(response1.responseText);
            let response2Html = this.parseHtml(response2.responseText);
            let revisedElements = [];
            let preset = null;
            if (esgst.df) {
                let name = esgst.df_preset;
                if (name) {
                    let presets = JSON.parse(esgst.storage.dfPresets);
                    let i;
                    for (i = presets.length - 1; i > -1 && presets[i].name !== name; i--);
                    if (i > -1) {
                        preset = presets[i];
                    }
                }
            }
            (await this.discussions_get(response1Html, true, savedDiscussions)).forEach(element => {
                if (element.category !== `Deals`) {
                    revisedElements.push(element);
                }
            });
            let i = revisedElements.length - (numDiscussions + filteredDiscussions + 1);
            while (numDiscussions < 5 && i > -1) {
                if (!preset || !this.df_filterDiscussion(preset, revisedElements[i])) {
                    setMissingDiscussion(revisedElements[i]);
                    rows[0].appendChild(revisedElements[i].outerWrap);
                    numDiscussions += 1;
                }
                i -= 1;
            }
            let elements = await this.discussions_get(response2Html, true, savedDiscussions);
            i = elements.length - (numDeals + filteredDeals + 1);
            while (numDeals < 5 && i > -1) {
                if (!preset || !this.df_filterDiscussion(preset, elements[i])) {
                    setMissingDiscussion(elements[i]);
                    rows[1].appendChild(elements[i].outerWrap);
                    numDeals += 1;
                }
                i -= 1;
            }
            if (esgst.adots) {
                this.adots_load(refresh);
            } else if (esgst.radb && !refresh) {
                this.radb_addButtons();
            }
            if (refresh) {
                await this.endless_load(esgst.activeDiscussions);
            }
            if (callback) {
                callback();
            }
        } else {
            if (esgst.adots) {
                this.adots_load();
            } else if (esgst.radb && !refresh) {
                this.radb_addButtons();
            }
            if (callback) {
                callback();
            }
        }
    }
